#include "Tree/Game.hpp"#include "Tree/Settings.hpp"#include "Tree/Butler.hpp"#include "Tree/Util.hpp"float Tree::GetFPS(){    return GAME->GetFPS();}const sf::Input &Tree::GetInput(){    return GAME->GetInput();}const int Tree::GetWindowWidth(){    return GAME->GetWindowWidth();}const int Tree::GetWindowHeight(){    return GAME->GetWindowHeight();}Vec2f Tree::GetMousePos(){    return GAME->GetMousePos();}void Tree::SetMousePos( float x, float y ){    GAME->SetMousePos( x, y );}void Tree::VisualDebug( std::string str ){    GAME->GetVisualDebug()->Add( str );}void Tree::VisualDebug( std::string id, std::string str ){    GAME->GetVisualDebug()->SetPersistent( id, str );}void Tree::RemoveDebug( std::string id ){    GAME->GetVisualDebug()->RemovePersistent( id );}boost::shared_ptr<Tree::Butler> Tree::GetButler(){    return GAME->GetButler();}boost::shared_ptr<Tree::Settings> Tree::GetSettings(){    return GAME->GetSettings();}boost::shared_ptr<Tree::Tweaks> Tree::GetTweaks(){    return GAME->GetTweaks();}boost::shared_ptr<Tree::LogHelper> Tree::GetLogHelper(){    return GAME->GetLogHelper();}